def sort_dict_by_desc(data:dict) -> dict:
    res_dict = {}
    data_list = sorted(data.items(), key=lambda x: x[1]["description"], reverse=False)
    for item in data_list:
        res_dict[item[0]] = item[1]
    return res_dict


